package com.montassar.timetoguessbasketball.Models;public class Games {    private int id;    private int gameImg;    private String gameName;    public Games(int id, int gameImg, String gameName) {        this.id = id;        this.gameImg = gameImg;        this.gameName = gameName;    }    public Games(int gameImg, String gameName) {        this.gameImg = gameImg;        this.gameName = gameName;    }    public int getId() {        return id;    }    public void setId(int id) {        this.id = id;    }    public int getGameImg() {        return gameImg;    }    public void setGameImg(int gameImg) {        this.gameImg = gameImg;    }    public String getGameName() {        return gameName;    }    public void setGameName(String gameName) {        this.gameName = gameName;    }}