package com.montassar.timetoguessbasketball.utils;import android.content.Context;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.BaseAdapter;import android.widget.TextView;import com.montassar.timetoguessbasketball.R;import java.util.ArrayList;import java.util.HashMap;import static com.montassar.timetoguessbasketball.fragments.GameFragment.KEY_LETTER_GAME;public class LetterAdapter extends BaseAdapter {    Context context;    protected ArrayList<HashMap<String, String>> data;    private static LayoutInflater inflater = null;    public LetterAdapter(Context context, ArrayList<HashMap<String,String>> data)    {        this.context = context;        this.data = data;        inflater = LayoutInflater.from(context);    }    @Override    public int getCount() {        return data.size();    }    @Override    public Object getItem(int position) {        return position;    }    @Override    public long getItemId(int position) {        return position;    }    @Override    public View getView(int position, View convertView, ViewGroup parent) {        View view = convertView;        if (convertView ==null)        {            view = inflater.inflate(R.layout.letter_row_grid,null);            TextView letterCase = (TextView) view.findViewById(R.id.letterButton);            HashMap<String,String> caseMap = new HashMap<String, String>();            caseMap = data.get(position);            letterCase.setText(caseMap.get(KEY_LETTER_GAME).toUpperCase());        }        return view;    }}