package com.montassar.timetoguessbasketball.utils;import android.content.Context;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.ImageView;import android.widget.TextView;import androidx.annotation.NonNull;import androidx.recyclerview.widget.RecyclerView;import com.montassar.timetoguessbasketball.Models.Games;import com.montassar.timetoguessbasketball.R;import java.util.ArrayList;public class MyGamesAdapter extends RecyclerView.Adapter<MyGamesAdapter.MyViewHolder> {    private ArrayList<Games> data;    private LayoutInflater inflater;    private GameClickListener GameClickListener;    public MyGamesAdapter(Context context, ArrayList<Games> data) {        this.data = data;        inflater = LayoutInflater.from(context);    }    @NonNull    @Override    public MyViewHolder onCreateViewHolder(@NonNull ViewGroup parent, int viewType) {        View view = inflater.inflate(R.layout.item_game,parent,false);        return new MyViewHolder(view);    }    @Override    public void onBindViewHolder(@NonNull MyViewHolder holder, int position) {        holder.init(data.get(position).getId(),data.get(position).getGameImg(),data.get(position).getGameName());    }    @Override    public int getItemCount() {        return data.size();    }    public class MyViewHolder extends RecyclerView.ViewHolder implements View.OnClickListener {        ImageView imgGameIcon;        TextView txtGameId,txtGameName;        public MyViewHolder(@NonNull View itemView) {            super(itemView);            imgGameIcon = (ImageView) itemView.findViewById(R.id.img_game_ic);            txtGameId = (TextView) itemView.findViewById(R.id.txt_id);            txtGameName= (TextView) itemView.findViewById(R.id.txt_game_name);            itemView.setOnClickListener(this);        }        public void init(int id, int img, String name)        {                txtGameId.setText(String.valueOf(id));                imgGameIcon.setImageResource(img);                txtGameName.setText(name);        }        @Override        public void onClick(View v) {            if (GameClickListener != null)            {                GameClickListener.onItemClickListener(v,getAdapterPosition());            }        }    }    public void setClickListener(GameClickListener questionClickListener)    {        this.GameClickListener = questionClickListener;    }    public  interface GameClickListener{        void onItemClickListener(View view, int position);    }}