package com.montassar.timetoguessbasketball.utils;import android.content.Context;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.ImageView;import android.widget.TextView;import androidx.annotation.NonNull;import androidx.recyclerview.widget.RecyclerView;import com.montassar.timetoguessbasketball.Models.Games;import com.montassar.timetoguessbasketball.R;import java.util.ArrayList;public class MyStoreAdapter extends RecyclerView.Adapter<MyStoreAdapter.MyViewHolder> {    private ArrayList<Games> data;    private LayoutInflater inflater;    private QuestionClickListener questionClickListener;    public MyStoreAdapter(Context context, ArrayList<Games> data) {        this.data = data;        inflater = LayoutInflater.from(context);    }    @NonNull    @Override    public MyViewHolder onCreateViewHolder(@NonNull ViewGroup parent, int viewType) {        View view = inflater.inflate(R.layout.item_store,parent,false);        return new MyViewHolder(view);    }    @Override    public void onBindViewHolder(@NonNull MyViewHolder holder, int position) {        holder.init(data.get(position).getGameImg(),data.get(position).getGameName());    }    @Override    public int getItemCount() {        return data.size();    }    public class MyViewHolder extends RecyclerView.ViewHolder implements View.OnClickListener {        ImageView imgQuestionBackground;        TextView txtQuestionNumber;        public MyViewHolder(@NonNull View itemView) {            super(itemView);            imgQuestionBackground = (ImageView) itemView.findViewById(R.id.img_question_background);            txtQuestionNumber = (TextView) itemView.findViewById(R.id.txt_question_num);            itemView.setOnClickListener(this);        }        public void init(int img, String name)        {                imgQuestionBackground.setImageResource(img);                txtQuestionNumber.setText(name);        }        @Override        public void onClick(View v) {            if (questionClickListener != null)            {                questionClickListener.onItemClickListener(v,getAdapterPosition());            }        }    }    public void setClickListener(QuestionClickListener questionClickListener)    {        this.questionClickListener = questionClickListener;    }    public  interface QuestionClickListener{        void onItemClickListener(View view, int position);    }}